document.addEventListener("DOMContentLoaded", () => {
  const slides = document.querySelectorAll(".slide");
  let currentSlide = 0;

  function showSlide(index) {
    slides.forEach((slide, i) => slide.classList.toggle("active", i === index));
  }

  document.querySelector(".prev-slide")?.addEventListener("click", () => {
    currentSlide = (currentSlide > 0) ? currentSlide - 1 : slides.length - 1;
    showSlide(currentSlide);
  });

  document.querySelector(".next-slide")?.addEventListener("click", () => {
    currentSlide = (currentSlide < slides.length - 1) ? currentSlide + 1 : 0;
    showSlide(currentSlide);
  });

  showSlide(currentSlide);

  const narracion = document.getElementById("voz-gonzalo");
  window.iniciarSecuencia = function () {
    document.getElementById("resultado-secuencia").textContent = "";
    narracion.currentTime = 0;
    narracion.play();
    inputUsuario = [];
  };

  const secuencia = [3, 2, 0, 1]; // âš”ï¸ âœï¸ ğŸ›¡ï¸ ğŸ•¯ï¸
  let inputUsuario = [];
  const simbolos = document.querySelectorAll(".simbolo");

  simbolos.forEach(s => {
    s.addEventListener("click", e => {
      const id = Number(e.target.dataset.id);
      inputUsuario.push(id);

      e.target.classList.add("activo");
      setTimeout(() => e.target.classList.remove("activo"), 400);

      if (inputUsuario[inputUsuario.length - 1] !== secuencia[inputUsuario.length - 1]) {
        document.getElementById("resultado-secuencia").textContent = "âŒ Fallaste. Intenta de nuevo.";
        playSound("error-sound");
        inputUsuario = [];
        return;
      }

      if (inputUsuario.length === secuencia.length) {
        document.getElementById("resultado-secuencia").textContent = "âœ… Â¡Secuencia correcta!";
        playSound("coins-sound");
        respuestasCastillo.secuencia = true;
        comprobarFinalCastillo();
      }
    });
  });

  window.checkVisualAnswer = function (respuesta, correcta, num) {
    const res = document.getElementById("visual-resultado-" + num);
    if (respuesta === correcta) {
      res.textContent = "âœ… Â¡Correcto!";
      respuestasCastillo["visual" + num] = true;
      playSound("coins-sound");
    } else {
      res.textContent = "âŒ Incorrecto. Intenta de nuevo.";
      playSound("error-sound");
    }
    comprobarFinalCastillo();
  };

  window.checkAnswer = function (respuesta, correcta, num) {
    const res = document.getElementById("quiz-resultado-" + num);
    if (respuesta === correcta) {
      res.textContent = "âœ… Â¡Correcto!";
      respuestasCastillo["quiz" + num] = true;
      playSound("coins-sound");
    } else {
      res.textContent = "âŒ Incorrecto.";
      playSound("error-sound");
    }
    comprobarFinalCastillo();
  };

  function comprobarFinalCastillo() {
    const total = Object.keys(respuestasCastillo).length;
    if (Object.keys(respuestasCastillo).length >= 4 || intentosSuficientes()) {
      document.getElementById("continue-button").style.display = "block";
    }
  }

  function playSound(id) {
    const el = document.getElementById(id);
    if (el) {
      el.currentTime = 0;
      el.play().catch(() => {});
    }
  }

  window.respuestasCastillo = {};
});


  function intentosSuficientes() {
    // Intentos mÃ­nimos para permitir continuar aunque no acierte
    const intentos = ["visual1", "quiz1", "quiz2", "quiz3", "secuencia"];
    return intentos.every(i => i in respuestasCastillo);
  }
